import React, { useState, useEffect } from "react";
import {View, 
        Text, 
        FlatList, 
        StyleSheet, 
        TouchableWithoutFeedback} from "react-native";

import Clipboard from '../../../node_modules/react-native-clipboard';

export default function Vouchers({ navigation, route }){
    const [voucherData, setVoucherData] = useState([]);
    const [loading, setLoading] = useState(true);
    var { id } = route.params;

  useEffect(() => {
    // Função para obter dados da API
    const fetchData = async () => {
      try {
      const response = await fetch(`https://api-mobile-25hv.onrender.com/api/Voucher/${id}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
              },
          });

        if (!response.ok) {
          throw new Error('Erro ao buscar dados! ');
        }
        const data = await response.json();
        setVoucherData(data);
      } catch (error) {
        console.error('Erro ao buscar dados:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const CopyableText = ({ text }) => {
    const handleCopyPress = () => {
      Clipboard.setString(text);
      alert('Texto copiado para a área de transferência');
    };
  
    return (
      <TouchableWithoutFeedback onPress={handleCopyPress}>
          
      </TouchableWithoutFeedback>
    );
  };


  const renderItem = ({ item }) => (
    <View style={styles.box}>
      <Text style={{ color: '#fff' }}>{item.voucher_token}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.text}>Meus vouchers</Text> 
      {loading ? (
        <Text>Carregando...</Text>
      ) : (
        <FlatList
          showsVerticalScrollIndicator={false}
          style={styles.list}
          data={voucherData}
          renderItem={renderItem}
          keyExtractor={(item) => item.voucher_id.toString()}
        />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container:{
    flex:1,
    alignItems:'center',
    justifyContent:'center',
    width: '100%',
    backgroundColor: '#000'
  },
  box: {
      padding: '20%', 
      backgroundColor: '#000',
    },
  list: {
      width: '100%',
  },

  text: {
      marginTop: '20%',
      color: '#fff'
  }
});
